<?phpinclude(__DIR__ . "/../system/config.inc.php");if(empty($_SERVER['HTTP_APPKEY'])) {    $result = array("response"=>"false","msg"=>"Please provide api key.","data"=>array());} else if($_SERVER['HTTP_APPKEY'] != APPKEY) {    $result = array("response"=>"false","msg"=>"Please provide valid api key.","data"=>array());} else if(!isset($_POST["order_id"]) || empty($_POST["order_id"])) {    $result = array("response"=>"false","msg"=>"Please provide order id.","data"=>array());} else {        $order_id = trim($_POST["order_id"]);         //$db->where("o.status", 'Pending');    //$db->where("o.driver_id", 0);    $db->where("o.id", $order_id);        $order = $db->getOne ("orders o");        if ($db->count > 0) {        if($order['driver_id'] == 0) {            $msg = "Driver not assigned yet!";            $result = array("response"=>"false","msg"=>$msg,"data"=> array('status'=>$order['status']));                       } else {            $result = array("response"=>"true","msg"=>"Order inprocess!","data"=>array('status'=>$order['status']));        }    } else {        $result = array("response"=>"false","msg"=>"Order not found!","data"=>array());    }}echo sendResponse($result);exit;