<?phpinclude(__DIR__ . "/../system/config.inc.php");if(empty($_SERVER['HTTP_APPKEY'])) {    $result = array("response"=>"false","msg"=>"Please provide api key.","data"=>array());} else if($_SERVER['HTTP_APPKEY'] != APPKEY) {    $result = array("response"=>"false","msg"=>"Please provide valid api key.","data"=>array());} else if(!isset($_POST["type"]) || empty($_POST["type"])) {    $result = array("response"=>"false","msg"=>"Please provide type.","data"=>array());} else {    $type = trim($_POST["type"]);        $result = array();    switch ($type) {        case "region" :            $db->where ("status", "Active");             $result = $db->get ("region");            break;        case "category" :            $db->where ("status", "Active");             $result = $db->get ("category");            break;        case "type" :            $db->where ("status", "Active");             $result = $db->get ("type");            break;    }        if ($db->count > 0) {                        $msg = "$type info!";            $result = array("response"=>"true","msg"=>$msg,"data"=> $result);                    } else {        $result = array("response"=>"false","msg"=>"Please provide valid type","data"=>array());    }}echo sendResponse($result);exit;