<?phpinclude(__DIR__ . "/../system/config.inc.php");if(empty($_SERVER['HTTP_APPKEY'])) {    $result = array("response"=>"false","msg"=>"Please provide api key.","data"=>array());} else if($_SERVER['HTTP_APPKEY'] != APPKEY) {    $result = array("response"=>"false","msg"=>"Please provide valid api key.","data"=>array());} else {           $db->where ("status", "Active");            $db->orderBy("min_distance","asc");    $fees = $db->get ("delivery_fees");    if ($db->count > 0) {                  $msg = "Delivery Charges!";        $result = array("response"=>"true","msg"=>$msg,"data"=> $fees);                    } else {        $result = array("response"=>"false","msg"=>"Not andy delivery charges found!","data"=>array());    }}echo sendResponse($result);exit;