<?phpinclude(__DIR__ . "/../system/config.inc.php");if(empty($_SERVER['HTTP_APPKEY'])) {    $result = array("response"=>"false","msg"=>"Please provide api key.","data"=>array());} else if($_SERVER['HTTP_APPKEY'] != APPKEY) {    $result = array("response"=>"false","msg"=>"Please provide valid api key.","data"=>array());} else if(!isset($_POST["order_id"]) || empty($_POST["order_id"])) {    $result = array("response"=>"false","msg"=>"Please provide order id.","data"=>array());} else {    $order_id = $_POST["order_id"];    $orequest = getDriverOrderRequest($order_id);        if(count($orequest) > 0) {        $result = array("response"=>"true","msg"=>"Order request","data"=>$orequest);    } else {        $result = array("response"=>"false","msg"=>"Order not found.","data"=>array());    }    }echo sendResponse($result);exit;