<?phpinclude(__DIR__ . "/../system/config.inc.php");if(empty($_SERVER['HTTP_APPKEY'])) {    $result = array("response"=>"false","msg"=>"Please provide api key.","data"=>array());} else if($_SERVER['HTTP_APPKEY'] != APPKEY) {    $result = array("response"=>"false","msg"=>"Please provide valid api key.","data"=>array());} else if(!isset($_POST["driver_id"]) || empty($_POST["driver_id"])) {    $result = array("response"=>"false","msg"=>"Please provide driver id.","data"=>array());} else if(!isset($_POST["uniqueid"]) || empty($_POST["uniqueid"])) {    $result = array("response"=>"false","msg"=>"Please provide unique id.","data"=>array());} else if(!isset($_POST["token"]) || empty($_POST["token"])) {    $result = array("response"=>"false","msg"=>"Please provide device token.","data"=>array());} else if(!isset($_POST["device_type"]) || empty($_POST["device_type"])) {    $result = array("response"=>"false","msg"=>"Please provide device type.","data"=>array());} else {    $driver_id = trim($_POST["driver_id"]);    $uniqueid = trim($_POST["uniqueid"]);    $token = trim($_POST["token"]);    $device_type = trim($_POST["device_type"]);       $date = date('Y-m-d H:i:s');        $db->where("driver_id",$driver_id);    $db->where("uniqueid",$uniqueid);    $db->get("driver_device",array(0,1),'id');    if($db->count > 0 ) {        $data = array(            'device_token' => $token,            'device_type' => $device_type,            'modified_date' => $date        );        $db->where('driver_id',$driver_id);        $db->where('uniqueid',$uniqueid);        $db->update("driver_device",$data);           $result = array("response"=>"true","msg"=>"Driver device updated successfully!","data"=> array());      } else {        $data = array(            'driver_id' => $driver_id,            'uniqueid' => $uniqueid,            'device_token' => $token,            'device_type' => $device_type,            'created_date' => $date,            'modified_date' => $date        );        $id = $db->insert ("driver_device",$data);        $result = array("response"=>"true","msg"=>"Driver device inserted successfully!","data"=> array());      }    }echo sendResponse($result);exit;