<?phpinclude(__DIR__ . "/../system/config.inc.php");if(empty($_SERVER['HTTP_APPKEY'])) {    $result = array("response"=>"false","msg"=>"Please provide api key.","data"=>array());} else if($_SERVER['HTTP_APPKEY'] != APPKEY) {    $result = array("response"=>"false","msg"=>"Please provide valid api key.","data"=>array());} else if(!isset($_POST["driver_id"]) || empty($_POST["driver_id"])) {    $result = array("response"=>"false","msg"=>"Please provide driver id.","data"=>array());} else if(!isset($_POST["order_id"]) || empty($_POST["order_id"])) {    $result = array("response"=>"false","msg"=>"Please provide user id.","data"=>array());} else {    $driver_id = trim($_POST["driver_id"]);     $order_id = trim($_POST["order_id"]);         $db->join("store s","o.store_id=s.id","LEFT");    $db->where("o.driver_id", $driver_id);         $db->where("o.id", $order_id);        $orders = $db->get ("orders o",array(0,1),'o.*,s.name as shop_name,s.owner as shop_owner, s.address as shop_address, s.phone as shop_phone,s.image as shop_image,CONCAT("'.STORE_PROFILE_IMG_URL.'",s.image) AS shop_image_url');        if ($db->count > 0) {             $db->join("products p","op.product_id=p.id","LEFT");        $db->join("type t","p.type_id=t.id","LEFT");                $db->where("op.order_id",$orders[0]['id']);                    $db->orderBy("op.id","asc");        $prod_detail = $db->get("order_products op",null,'op.order_id,op.product_id,op.product_name,op.price,op.quantity,op.attribute_description,p.image,CONCAT("'.PRODUCT_IMG_URL.'",p.`image`) AS `image_url`,t.name as type,t.color');        $orders[0]['total_products'] = count($prod_detail);        //echo $db->getLastQuery(); exit;        $orders[0]['products'] = $prod_detail;                        $msg = "Order detail!";        $result = array("response"=>"true","msg"=>$msg,"data"=> $orders);                   } else {        $result = array("response"=>"false","msg"=>"Not any order found!","data"=>array());    }}echo sendResponse($result);exit;