<?phpinclude(__DIR__ . "/../system/config.inc.php");if(empty($_SERVER['HTTP_APPKEY'])) {    $result = array("response"=>"false","msg"=>"Please provide api key.","data"=>array());} else if($_SERVER['HTTP_APPKEY'] != APPKEY) {    $result = array("response"=>"false","msg"=>"Please provide valid api key.","data"=>array());} else if(!isset($_POST["code"]) || empty($_POST["code"])) {    $result = array("response"=>"false","msg"=>"Please provide verification code same as in your email.","data"=>array());} else if(!isset($_POST["driver_id"]) || empty($_POST["driver_id"])) {    $result = array("response"=>"false","msg"=>"Please provide driver id.","data"=>array());} else {    $token = trim($_POST["code"]);    $userid = $_POST["driver_id"];        $db->where ("token", $token);    $db->where ("id", $userid);    $user = $db->getOne ("driver");    if ($db->count > 0) {            $db->where ("email", $user['email']);        $db->where ("status", 'Active');        $user1 = $db->getOne ("driver");        if($db->count > 0) {           $result = array("response"=>"false","msg"=>"Error! One of the account with using your email address is already activated.");        } else {            $data = Array (                'status' => 'Active',                'token' => ''            );            $db->where ('id', $userid);            $db->update ('driver', $data);            //delete all inactive user with same email id            $db->where ("email", $user['email']);            $db->where ("status", 'Pending');            $db->delete ('driver');            $msg = "Success! Your account verified. Now your account activated.";            $result = array("response"=>"true","msg"=>$msg,"data"=>array("id"=>$userid));        }    } else {        $result = array("response"=>"false","msg"=>"The code you have entered is mismatch. Please enter valid code from your email.","data"=>array());    }}echo sendResponse($result);exit;?>