<?phpinclude(__DIR__ . "/../system/config.inc.php");if(empty($_SERVER['HTTP_APPKEY'])) {    $result = array("response"=>"false","msg"=>"Please provide api key.","data"=>array());} else if($_SERVER['HTTP_APPKEY'] != APPKEY) {    $result = array("response"=>"false","msg"=>"Please provide valid api key.","data"=>array());} else if(!isset($_POST["email"]) || empty($_POST["email"])) {    $result = array("response"=>"false","msg"=>"Please provide email.","data"=>array());} else if(!isset($_POST["password"]) || empty($_POST["password"])) {    $result = array("response"=>"false","msg"=>"Please provide password.","data"=>array());} else {    $email = trim($_POST["email"]);    $password = trim($_POST["password"]);        $db->where ("email", $email);    $db->where ("password", md5($password));    $user = $db->getOne ("user");    if ($db->count > 0) {            if($user['status'] == "Active") {            $approvemsg = "";            switch ($user['adminApproved']) {                case "Pending":                    $approvemsg = "We will verify your personal details and documents once completed that process then you can access this application.";                    break;                case "Rejected":                    $rejectreason = str_replace(","," and ", $user['adminRejectReason']);                    $approvemsg = "We was verified your personal detail and documents admin was rejected your $rejectreason dcouments, please again provide valid documents and access this application.";                    break;            }            $user['verifymsg'] = $approvemsg;            $msg = "Success.";            $result = array("response"=>"true","msg"=>$msg,"data"=>$user);        } else {            $msg = "Your account is inactive.";            $result = array("response"=>"false","msg"=>$msg,"data"=> array());        }            } else {        $result = array("response"=>"false","msg"=>"Please provide valid email or password.","data"=>array());    }}echo sendResponse($result);exit;?>