<?phpinclude(__DIR__ . "/../system/config.inc.php");if(empty($_SERVER['HTTP_APPKEY'])) {    $result = array("response"=>"false","msg"=>"Please provide api key.","data"=>array());} else if($_SERVER['HTTP_APPKEY'] != APPKEY) {    $result = array("response"=>"false","msg"=>"Please provide valid api key.","data"=>array());} else if(!isset($_POST["token"]) || empty($_POST["token"])) {    $result = array("response"=>"false","msg"=>"Please provide token.","data"=>array());} else if(!isset($_POST["user_id"]) || empty($_POST["user_id"])) {    $result = array("response"=>"false","msg"=>"Please provide user id.","data"=>array());} else {    $token = trim($_POST["token"]);    $user_id = trim($_POST["user_id"]);        $db->where ("device_token", $token);    $db->where ("user_id", $user_id);    $user_device = $db->getOne ("user_device");    if ($db->count > 0) {            $db->where("device_token",$token);        $db->where("user_id",$user_id);        $del_device = $db->delete("user_device");         $result = array("response"=>"true","msg"=>"Logout successfully!","data"=>array());    } else {        $result = array("response"=>"false","msg"=>"User device not found!","data"=>array());    }}echo sendResponse($result);exit;?>