<?phpinclude(__DIR__ . "/../system/config.inc.php");if(empty($_SERVER['HTTP_APPKEY'])) {    $result = array("response"=>"false","msg"=>"Please provide api key.","data"=>array());} else if($_SERVER['HTTP_APPKEY'] != APPKEY) {    $result = array("response"=>"false","msg"=>"Please provide valid api key.","data"=>array());} else if(!isset($_POST["name"]) || empty($_POST["name"])) {    $result = array("response"=>"false","msg"=>"Please provide name.","data"=>array());} else if(!isset($_POST["email"]) || empty($_POST["email"])) {    $result = array("response"=>"false","msg"=>"Please provide email.","data"=>array());} else if(!isset($_POST["zipcode"]) || empty($_POST["zipcode"])) {    $result = array("response"=>"false","msg"=>"Please provide zipcode.","data"=>array());} else if(!isset($_POST["mobile"]) || empty($_POST["mobile"])) {    $result = array("response"=>"false","msg"=>"Please provide mobile.","data"=>array());} else if(!isset($_POST["password"]) || empty($_POST["password"])) {    $result = array("response"=>"false","msg"=>"Please provide password.","data"=>array());} else {    $email = $_POST["email"];    $name = $_POST["name"];        $db->where ("email", $email);    $db->where ("status", 'Active');    $user = $db->getOne ("user");    if ($db->count <= 0) {            $date = date("Y-m-d H:i:s");        $token = generateKey(10);            $data = array(            "name"=>trim($name),                    "email"=>trim($email),            "zipcode"=>trim($_POST["zipcode"]),            "mobile"=>trim($_POST["mobile"]),            "password"=>md5(trim($_POST["password"])),               "status"=>"Pending",            "created_date"=>$date,            "token"=>$token        );            $userid = $db->insert ('user', $data);                       $db->where("id",4);        $template = $db->getOne("email_template");                $body = $template['email_body'];                    $find = array("{#name#}","{#code#}");        $replace = array("$name","$token");        $body = str_replace($find,$replace,$body);        $subject = $template['subject'];        #$email = "testineed@gmail.com";        #echo $body; exit;        send_mail($email, $subject, $body);        $msg = "Success! Please check your email for verfication code for activate your account.";        $result = array("response"=>"true","msg"=>$msg,"data"=>array("id"=>$userid));    } else {        $result = array("response"=>"false","msg"=>"The email address you have entered is already registered. Please try with different email.","data"=>array("id"=>""));    }}echo sendResponse($result);exit;?>