<?phpinclude(__DIR__ . "/../system/config.inc.php");if(empty($_SERVER['HTTP_APPKEY'])) {    $result = array("response"=>"false","msg"=>"Please provide api key.","data"=>array());} else if($_SERVER['HTTP_APPKEY'] != APPKEY) {    $result = array("response"=>"false","msg"=>"Please provide valid api key.","data"=>array());} else if(!isset($_POST["user_id"]) || empty($_POST["user_id"])) {    $result = array("response"=>"false","msg"=>"Please provide user id.","data"=>array());} else {    $userid = $_POST["user_id"];             $db->where ("id", $userid);        $user = $db->getOne ("user");           if ($db->count > 0) {                    $token = generateKey(10);                      $data = Array (            'token' => $token        );               $db->where ("id", $userid);          $db->update ('user', $data);        //        $body = '<p>Hello {#name#},</p><p>We have received request to verification code.</p><p>Your verification code is : {#code#}</p><br><br>';    //        $body = str_replace('{#name#}', $user['name'], $body);    //        $body = str_replace('{#code#}', $token, $body);//        $subject = "New Verification Code - Weed High5";                $db->where("id",5);        $template = $db->getOne("email_template");                $body = $template['email_body'];                    $find = array("{#name#}","{#code#}");        $replace = array($user['name'],"$token");        $body = str_replace($find,$replace,$body);        $subject = $template['subject'];                send_mail($user['email'], $subject, $body);        $msg = "Success! Please check your email for verfication code for activate your account.";        $result = array("response"=>"true","msg"=>$msg,"data"=>array("id"=>$userid));    } else {        $result = array("response"=>"false","msg"=>"User not found! Provide valid user id","data"=>array());    }}echo sendResponse($result);exit;