<?phpinclude(__DIR__ . "/../system/config.inc.php");    $driver_id = $_GET["driver_id"];    $lat = $_GET["latitude"];    $lng = $_GET["longitude"];        $db->where ("id", $driver_id);    //$db->where ("status", 'Active');    $driver = $db->getOne ("driver");    if ($db->count > 0) {            if($driver['status']=="Active") {                          $db->where("driver_id",$driver_id);            $position = $db->getOne("driver_location");            if($db->count<=0){                $data = array(                    "driver_id"=>$driver_id,                    "created_date"=>date("Y-m-d H:i:s")                );                $pos_id = $db->insert("driver_location",$data);                $position['id'] = $pos_id;                            }                       $address = getAddress($lat,$lng);                        $pdata = array(                "driver_id"=>$driver_id,                "latitude"=>$lat,                "longitude"=>$lng,                "address"=>$address,                "updated_date"=>date("Y-m-d H:i:s")            );            $db->where("id",$position['id']);            $db->where("driver_id",$driver_id);            if($db->update("driver_location",$pdata)) {                $result = array("response"=>"true","msg"=>"Driver location updated","data"=>array("driver_id"=>$driver_id));            } else {                $result = array("response"=>"false","msg"=>"Error! failed driver location update.","data"=>array());            }                       } else {           $result = array("response"=>"false","msg"=>"Driver status is inactive.","data"=>array());         }    } else {        $result = array("response"=>"false","msg"=>"Driver not found.","data"=>array());    }echo sendResponse($result);exit;?>