<?phpinclude(__DIR__ . "/../system/config.inc.php");if(empty($_SERVER['HTTP_APPKEY'])) {    $result = array("response"=>"false","msg"=>"Please provide api key.","data"=>array());} else if($_SERVER['HTTP_APPKEY'] != APPKEY) {    $result = array("response"=>"false","msg"=>"Please provide valid api key.","data"=>array());} else if(!isset($_POST["user_id"]) || empty($_POST["user_id"])) {    $result = array("response"=>"false","msg"=>"Please provide user id.","data"=>array());} else if(!isset($_POST["action"]) || empty($_POST["action"])) {    $result = array("response"=>"false","msg"=>"Please provide action.","data"=>array());    } else if(isset($_POST["action"]) && ($_POST["action"] == "edit" || $_POST["action"] == "delete") && (!isset($_POST["address_id"]) || empty($_POST["address_id"]))) {    $result = array("response"=>"false","msg"=>"Please provide address id.","data"=>array());    } else {         if($_POST["action"] == "add" || $_POST["action"] == "edit") {                     if(!isset($_POST["firstname"]) || empty($_POST["firstname"])) {            $result = array("response"=>"false","msg"=>"Please provide firstname.","data"=>array());        } else if(!isset($_POST["lastname"]) || empty($_POST["lastname"])) {            $result = array("response"=>"false","msg"=>"Please provide lastname.","data"=>array());        } else if(!isset($_POST["address"]) || empty($_POST["address"])) {            $result = array("response"=>"false","msg"=>"Please provide address.","data"=>array());        } else if(!isset($_POST["city"]) || empty($_POST["city"])) {            $result = array("response"=>"false","msg"=>"Please provide city.","data"=>array());        } else if(!isset($_POST["region"]) || empty($_POST["region"])) {            $result = array("response"=>"false","msg"=>"Please provide region.","data"=>array());        } else if(!isset($_POST["zipcode"]) || empty($_POST["zipcode"])) {            $result = array("response"=>"false","msg"=>"Please provide zipcode.","data"=>array());        } else if(!isset($_POST["state"]) || empty($_POST["state"])) {            $result = array("response"=>"false","msg"=>"Please provide state.","data"=>array());        } else if(!isset($_POST["phone"]) || empty($_POST["phone"])) {            $result = array("response"=>"false","msg"=>"Please provide phone.","data"=>array());        } else if(!isset($_POST["latitude"]) || empty($_POST["latitude"])) {            $result = array("response"=>"false","msg"=>"Please provide latitude.","data"=>array());        } else if(!isset($_POST["longitude"]) || empty($_POST["longitude"])) {            $result = array("response"=>"false","msg"=>"Please provide longitude.","data"=>array());        } else {             $data = array(                'user_id' => $_POST["user_id"],                'firstname' => $_POST["firstname"],                'lastname' => $_POST["lastname"],                'address' => trim($_POST["address"]),                'street' => '',                'city' => $_POST["city"],                'region' => $_POST["region"],                'zipcode' => $_POST["zipcode"],                'state' => $_POST["state"],                'country' => "US",                'phone' => $_POST["phone"],                'latitude' => $_POST["latitude"],                'longitude' => $_POST["longitude"]            );                       if($_POST["action"] == "add") {        // add user address                $id = $db->insert ('user_address', $data);                                              } else if($_POST["action"] == "edit") { // update user address                $db->where ('id', $_POST['address_id']);                $db->update ('user_address', $data);                    }            //$addresses = getUserAddresses($_POST["user_id"]);                    $result = array("response"=>"true","msg"=>"Addresses","data"=>array());         }    } else if ($_POST["action"] == "delete") { //delete user address        $data['status'] = 'Deleted';        $db->where ('id', $_POST['address_id']);        $db->update('user_address', $data);        $addresses = getUserAddresses($_POST["user_id"]);                $result = array("response"=>"true","msg"=>"Addresses","data"=>$addresses);    } else {         $addresses = getUserAddresses($_POST["user_id"]);                $result = array("response"=>"true","msg"=>"Addresses","data"=>$addresses);      }}echo sendResponse($result);exit;