<?phpinclude(__DIR__ . "/../system/config.inc.php");if(empty($_SERVER['HTTP_APPKEY'])) {    $result = array("response"=>"false","msg"=>"Please provide api key.","data"=>array());} else if($_SERVER['HTTP_APPKEY'] != APPKEY) {    $result = array("response"=>"false","msg"=>"Please provide valid api key.","data"=>array());} else if(!isset($_POST["user_id"]) || empty($_POST["user_id"])) {    $result = array("response"=>"false","msg"=>"Please provide user id.","data"=>array());} else if(!isset($_POST["page"]) || empty($_POST["page"])) {    $result = array("response"=>"false","msg"=>"Please provide page.","data"=>array());} else {    $user_id = trim($_POST["user_id"]);     $page = $_POST["page"];            $db->where("o.user_id", $user_id);        //$db->where("o.status","Pending");          $db->where('o.status', Array('Pending','Inprocess'), 'IN');    $db->orderBy("o.order_date","desc");    $db->pageLimit = 5;    $orders = $db->arraybuilder()->paginate ("orders o",$page,"o.id,o.order_code,o.user_id,o.driver_id,o.store_id,o.order_date,o.final_total,o.status");        $totalPages = $db->totalPages;            if ($db->count > 0) {             for($i=0;$i<count($orders);$i++) {            $db->join("products p","op.product_id=p.id","LEFT");            $db->join("type t","p.type_id=t.id","LEFT");            $db->where("op.order_id",$orders[$i]['id']);                        $db->orderBy("op.id","asc");            $prod_detail = $db->get("order_products op",null,'op.order_id,op.product_id,op.product_name,op.price,op.quantity,op.attribute_description,p.image,CONCAT("'.PRODUCT_IMG_URL.'",p.`image`) AS `image_url`,t.name as type,t.color');            $orders[$i]['total_products'] = count($prod_detail);            //echo $db->getLastQuery(); exit;            $orders[$i]['products'] = $prod_detail;        }        $msg = "Order info!";        $result = array("response"=>"true","msg"=>$msg,"data"=> $orders,"totalPages"=>$totalPages);                   } else {        $result = array("response"=>"false","msg"=>"Not any current order found!","data"=>array());    }}echo sendResponse($result);exit;