<?phpinclude(__DIR__ . "/../system/config.inc.php");if(empty($_SERVER['HTTP_APPKEY'])) {    $result = array("response"=>"false","msg"=>"Please provide api key.","data"=>array());} else if($_SERVER['HTTP_APPKEY'] != APPKEY) {    $result = array("response"=>"false","msg"=>"Please provide valid api key.","data"=>array());} else if(!isset($_POST["user_id"]) || empty($_POST["user_id"])) {    $result = array("response"=>"false","msg"=>"Please provide user id.","data"=>array());} else {    $user_id = trim($_POST["user_id"]);     //$order_id = trim($_POST["order_id"]);     $dist = ',111.111 * DEGREES(ACOS(COS(RADIANS(dl.latitude))         * COS(RADIANS(o.delivery_latitude))         * COS(RADIANS(dl.longitude - o.delivery_longitude))         + SIN(RADIANS(dl.latitude))         * SIN(RADIANS(o.delivery_latitude)))) AS distance';        $db->join("store s","o.store_id=s.id","LEFT");    $db->join("driver d","o.driver_id=d.id","LEFT");    $db->join("driver_location dl","d.id=dl.driver_id","LEFT");    $db->where("o.user_id", $user_id);     //$db->where("o.id", $order_id);       $db->where('o.status', Array('Pending','Inprocess'), 'IN');    $db->orderBy("o.order_date","desc");    $orders = $db->get ("orders o",array(0,1),'o.id,o.order_code,o.user_id,o.driver_id,o.store_id,o.order_date,o.final_total,o.status,o.delivery_name,o.delivery_phone,o.delivery_address,o.delivery_latitude,o.delivery_longitude,d.name as driver_name,d.image,CONCAT("'.USER_UPLOAD_URL.'",d.`image`) AS `image_url`,d.car_number,d.car_brand,d.mobile,dl.latitude as driver_latitude, dl.longitude as driver_longitude,s.latitude as store_latitude,s.longitude as store_longitude,s.address'.$dist);               if ($db->count > 0) {                     $time =  ($orders[0]['distance']/50);        $time = gmdate("H:i", $time*3600);        $orders[0]['time'] = $time;        $msg = "Order info!";        $result = array("response"=>"true","msg"=>$msg,"data"=> $orders);                   } else {        $result = array("response"=>"false","msg"=>"Not found current order!","data"=>array());    }}echo sendResponse($result);exit;